var checkPalindrome=function(t){return t.length<2?!0:t.charAt(0)===t.charAt(t.length-1)?checkPalindrome(t.substring(1,t.length-1)):!1};$(function(){$("#check-str-button").click(function(){palstring=$("#entry-chars").val(),paltest=checkPalindrome(palstring)?"Yes, "+palstring+" is a palendrome":"No, "+palstring+" is not a palendrome",$("#pal-output").text(paltest)})});